def _${id}_probe():
    while True:
        <% obj = 'self' + ('.' + block_id if block_id else '') %>
        val = ${obj}.${function_name}(${function_args})
        try:
            self.set_${id}(val)
        except AttributeError:
            pass
        time.sleep(1.0 / (${poll_rate}))
_${id}_thread = threading.Thread(target=_${id}_probe)
_${id}_thread.daemon = True
_${id}_thread.start()
